const express = require('express');
const router = express.Router();
const BookController = require('../controllers/BookController');

router.get('/', BookController.getAllBooks);
router.post('/', BookController.addBook);
router.get('/:id', BookController.getBook);
router.put('/:id', BookController.updateBook);
router.delete('/:id', BookController.deleteBook);

module.exports = router;

const BookService = require('../services/BookService');
const Util = require('../utils/Utils');

const util = new Util();

class BookController {
  static async getAllBooks(req, res) {
    try {
      const allBooks = await BookService.getAllBooks();
      if (allBooks.length > 0) {
        util.setSuccess(200, 'Books retrieved', allBooks);
      } else {
        util.setSuccess(200, 'No book found');
      }

      return util.send(res);
    }
    catch (error) {
      util.setError(400, error);
      return util.send(res);
    }
  }

  static async addBook(req, res) {
    if (!req.body.title || !req.body.price || !req.body.description) {
      util.setError(400, 'Please provide complete details');
      return util.send(res);
    }

    const newBook = req.body;

    try {
      const createdBook = await BookService.addBook(newBook);
      util.setSuccess(201, 'Book Added!', createdBook);

      return util.send(res);
    }
    catch (error) {
      util.setError(400, error.message);
      return util.send(res);
    }
  }

  static async updateBook(req, res) {
    const alteredBook = req.body;
    const { id } = req.params;

    if (!Number(id)) {
      util.setError(400, 'Please input a valid numeric value');
      return util.send(res);
    }

    try {
      const updatedBook = await BookService.updateBook(id, alteredBook);

      if (!updatedBook) {
        util.setError(404, `Cannot find book with the id: ${id}`);
      } else {
        util.setSuccess(200, 'Book updated', updatedBook);
      }

      return util.send(res);
    }
    catch (error) {
      util.setError(404, error);
      return util.send(res);
    }
  }

  static async getBook(req, res) {
    const { id } = req.params;

    if (!Number(id)) {
      util.setError(400, 'Please input a valid numeric value');
      return util.send(res);
    }

    try {
      const book = await BookService.getBook(id);

      if (!book) {
        util.setError(404, `Cannot find book with the id ${id}`);
      } else {
        util.setSuccess(200, 'Found book', book);
      }

      return util.send(res);
    }
    catch (error) {
      util.setError(404, error);
      return util.send(res);
    }
  }

  static async deleteBook(req, res) {
    const { id } = req.params;

    if (!Number(id)) {
      util.setError(400, 'Please input a valid numeric value');
      return util.send(res);
    }

    try {
      const book = await BookService.deleteBook(id);

      if (book) {
        util.setSuccess(200, 'Book is deleted');
      } else {
        util.setError(404, `Book with id ${id} cannot be found`);
      }

      return util.send(res);
    }
    catch (error) {
      util.setError(400, error);
      return util.send(res);
    }
  }
}

module.exports = BookController;

const models = require('../src/models');

class BookService {
  static async getAllBooks() {
    try {
      return await models.Book.findAll();
    }
    catch (error) { throw new Error(error) }
  }

  static async addBook(newBook) {
    try {
      return await models.Book.create(newBook);
    }
    catch (error) { throw new Error(error) }
  }

  static async updateBook(id, updatedBook) {
    try {
      const bookToUpdate = await models.Book.findOne({
        where: { id: Number(id) }
      });

      if (bookToUpdate) {
        await models.Book.update(updatedBook, {
          where: { id: Number(id) }
        });

        return updatedBook;
      }
    }
    catch (error) { throw new Error(error) }
  }

  static async getBook(id) {
    try {
      const book = await models.Book.findOne({
        where: {  id: Number(id)}
      });

      return book;
    }
    catch (error) { throw new Error(error) }
  }

  static async deleteBook(id) {
    try {
      const book = await models.Book.findOne({
        where: { id: Number(id) }
      });

      if (book) {
        const deletedBook = await models.Book.destroy({
          where: { id: Number(id) }
        });

        return deletedBook;
      }

      return null;
    }
    catch (error) { throw new Error(error) }
  }
}

module.exports = BookService;

'use strict';

const fs = require('fs');
const path = require('path');
const Sequelize = require('sequelize');
const basename = path.basename(__filename);
const env = process.env.NODE_ENV || 'development';
const config = require(__dirname + '/../config/config.js')[env];
const db = {};

let sequelize;
if (config.use_env_variable) {
  sequelize = new Sequelize(process.env[config.use_env_variable], config);
} else {
  sequelize = new Sequelize(config.database, config.username, config.password, config);
}

fs
  .readdirSync(__dirname)
  .filter(file => {
    return (file.indexOf('.') !== 0) && (file !== basename) && (file.slice(-3) === '.js');
  })
  .forEach(file => {
    const model = sequelize['import'](path.join(__dirname, file));
    db[model.name] = model;
  });

Object.keys(db).forEach(modelName => {
  if (db[modelName].associate) {
    db[modelName].associate(db);
  }
});

db.sequelize = sequelize;
db.Sequelize = Sequelize;

module.exports = db;

